import { Component, Injector, Input } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { BoardTask, DateFormaterService, Priority, PriorityConvertorService } from '../../../../shared';
import { TaskInfoComponent, TaskManagerComponent, TaskPopupData, TaskService } from '../../../index';

/** The component that renders the task and provides access to edit, info menus.*/
@Component({
  selector: 'task',
  templateUrl: './task.component.html',
  styleUrl: './task.component.scss'
})
export class TaskComponent {
  @Input({ required: true }) task!: BoardTask;
  @Input({ required: true }) boardId!: string;

  constructor(public dialog: MatDialog,
    private dateFormater: DateFormaterService,
    private taskService: TaskService,
    private injector: Injector) { }

  openInfoMenu() {
    const dialogRef = this.dialog.open(TaskInfoComponent, {
      data: this.getPopupData(),
      injector: this.injector
    });
  }
  openEditMenu() {
    const dialogRef = this.dialog.open(TaskManagerComponent, {
      data: this.getPopupData()
    });
  }
  deleteTask() {
    this.taskService.deleteTask(this.task);
  }
  getPriorityString(priority: Priority): string {
    return PriorityConvertorService.getPriorityString(priority);
  }
  getFormatedDate(date: Date | undefined) {
    return this.dateFormater.formatDate(date);
  }
  private getPopupData() {
    const taskPopupData: TaskPopupData = {
      task: this.task,
      taskListId: this.task.boardTaskListId,
      boardId: this.boardId
    }
    return taskPopupData;
  }
}

import { ChangeDetectionStrategy, ChangeDetectorRef, Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';
import { Observable } from 'rxjs';
import { ActivityService } from '../../../../action-history';
import { BoardTask, BoardTaskActivity, BoardTaskList, DateFormaterService, Priority, PriorityConvertorService } from '../../../../shared';
import { TaskListService, TaskManagerComponent, TaskPopupData } from '../../../index';

/** The component that shows more information about the task.*/
@Component({
  selector: 'app-task-info',
  templateUrl: './task-info.component.html',
  styleUrl: './task-info.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class TaskInfoComponent {
  task!: BoardTask;
  currentTaskList$!: Observable<BoardTaskList | undefined>;
  taskActivities$!: Observable<BoardTaskActivity[] | undefined>;

  constructor(public dialog: MatDialog,
    @Inject(MAT_DIALOG_DATA) private data: TaskPopupData,
    private taskListService: TaskListService,
    private sanitizer: DomSanitizer,
    private activityService: ActivityService,
    private dateFormater: DateFormaterService,
    private cdr: ChangeDetectorRef) {
  }

  ngOnInit(): void {
    this.task = this.data.task!;
    this.currentTaskList$ = this.taskListService.getTaskListById(this.task.boardTaskListId!);
    this.taskActivities$ = this.activityService.getTaskActivitiesByTaskId(this.task.id);
  }
  openEditMenu() {
    const dialogRef = this.dialog.open(TaskManagerComponent, {
      data: this.data
    });
  }
  getPriorityString(priority: Priority): string {
    return PriorityConvertorService.getPriorityString(priority);
  }
  getFormatedDate(date: Date | undefined) {
    return this.dateFormater.formatDate(date);
  }
  sanitizeHtml(html: string): SafeHtml {
    return this.sanitizer.bypassSecurityTrustHtml(html);
  }
  updateView() {
    this.cdr.markForCheck();
  }
}

import { CdkDragDrop } from '@angular/cdk/drag-drop';
import { Component, Input } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { BoardTask, BoardTaskList } from '../../../../shared';
import { TaskListManagerComponent, TaskListService, TaskListsPopupData, TaskManagerComponent, TaskPopupData, TaskService } from '../../../index';
/**The component that represents task list. Could call deleting, adding, information task and for editing own task list data menus.*/
@Component({
  selector: 'tasks-list',
  templateUrl: './task-list.component.html',
  styleUrl: './task-list.component.scss',
})
export class TaskListComponent {
  @Input() taskList: BoardTaskList | undefined;
  @Input({ required: true }) boardId!: string;

  constructor(private dialog: MatDialog,
    private taskListService: TaskListService,
    private taskService: TaskService) { }

  onTaskDrop(event: CdkDragDrop<BoardTaskList>) {
    var task: BoardTask = {
      ...event.item.data,
      boardTaskListId: event.container.data.id
    };
    this.taskService.updateTask(event.previousContainer.data, task, event.currentIndex);
  }
  openListManagerMenu() {
    const dialogRef = this.dialog.open(TaskListManagerComponent, {
      data: this.getPopupListData()
    });
    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        const isNew = this.taskList == undefined;
        if (isNew) {
          this.taskListService.createTaskList(result);
        }
        else {
          this.taskListService.updateTaskList(result);
        }
      }
    });
  }
  openNewTaskManagerMenu() {
    const taskManagerData: TaskPopupData = {
      task: undefined,
      taskListId: this.taskList?.id!,
      boardId: this.boardId
    }
    const dialogRef = this.dialog.open(TaskManagerComponent, {
      data: taskManagerData,
    });
  }
  deleteTaskList() {
    this.taskListService.deleteTaskList(this.taskList!);
  }
  private getPopupListData() {
    var data: TaskListsPopupData = {
      taskList: this.taskList,
      boardId: this.boardId
    }
    return data;
  }
}

import { ChangeDetectionStrategy, ChangeDetectorRef, Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { BoardTaskList, getDefaultBoardTaskList } from '../../../../shared';
import { TaskListsPopupData } from '../../../index';

/**The component for editing/creating a task list.*/
@Component({
  selector: 'app-task-list-manager',
  templateUrl: './task-list-manager.component.html',
  styleUrl: './task-list-manager.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class TaskListManagerComponent implements OnInit {
  taskListForm!: FormGroup;
  cardName: string = "";
  private taskList!: BoardTaskList;

  constructor(@Inject(MAT_DIALOG_DATA) public data: TaskListsPopupData,
    private formBuilder: FormBuilder,
    private dialogRef: MatDialogRef<TaskListManagerComponent>,
    private cdr: ChangeDetectorRef) { }

  ngOnInit(): void {
    this.cardName = this.data.taskList ? "Edit List" : "Create List";
    this.assignTaskList();
    this.initForm();
  }
  onSubmit(): void {
    if (this.taskListForm.valid) {
      var taskListFromForm = this.getDataFromForm();
      this.dialogRef.close(taskListFromForm);
    }
  }
  isNameInvalid() {
    return this.taskListForm.get("name")?.invalid && this.taskListForm.get("name")?.dirty;
  }
  updateView() {
    this.cdr.markForCheck();
  }
  private assignTaskList() {
    this.taskList = this.data.taskList ? this.data.taskList : getDefaultBoardTaskList();
  }
  private initForm(): void {
    this.taskListForm = this.formBuilder.group({
      name: [this.taskList?.name || '', Validators.required]
    });
  }
  private getDataFromForm() {
    const formValue = this.taskListForm.value;
    const buffer = { ...this.taskList, name: formValue.name, boardId: this.data.boardId };
    return buffer;
  }
}

import { ChangeDetectionStrategy, Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { BoardTask, BoardTaskList, DateValidator, Priority, PriorityConvertorService, getDefaultBoardTask } from '../../../../shared';
import { TaskListService, TaskPopupData, TaskService } from '../../../index';

/** The component for editing/creating a task. Takes data task from mat dialog data.*/
@Component({
  selector: 'app-task-manager',
  templateUrl: './task-manager.component.html',
  styleUrl: './task-manager.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class TaskManagerComponent implements OnInit {
  taskForm!: FormGroup;
  cardName!: string;
  minDate!: Date;
  isNewTask!: boolean;
  allTaskLists!: BoardTaskList[];
  private task!: BoardTask;

  constructor(@Inject(MAT_DIALOG_DATA) private data: TaskPopupData,
    private taskListService: TaskListService,
    private formBuilder: FormBuilder,
    private dialogRef: MatDialogRef<TaskManagerComponent>,
    private taskService: TaskService,
    private dateValidator: DateValidator) {
  }

  ngOnInit(): void {
    this.isNewTask = this.data.task == undefined;
    this.cardName = this.isNewTask ? "Create Task" : "Edit Task";
    this.setMinDate();
    this.getTaskLists();
    this.assignTask();
    this.initForm();
  }
  getAllPriorityNames() {
    return PriorityConvertorService.getAllPrioritiesString()
  }
  onSubmit(): void {
    if (this.taskForm.valid) {
      var taskInForm = this.getTaskFromForm();
      if (this.isNewTask)
        this.createNewTask(taskInForm);
      else
        this.updateTask(taskInForm);
      this.dialogRef.close(taskInForm);
    }
  }
  isDateInvalid() {
    return this.taskForm.get("dueDate")?.invalid && this.taskForm.get("dueDate")?.dirty;
  }
  isNameInvalid() {
    return this.taskForm.get("name")?.invalid && (this.taskForm.get("name")?.dirty || this.taskForm.get("name")?.touched);
  }
  private setMinDate() {
    var date = this.isNewTask ? new Date() : this.data.task?.dueTime ?? new Date();
    this.minDate = date;
  }
  private getTaskLists() {
    this.taskListService.getTaskListsByBoardId(this.data.boardId).subscribe((lists) => { this.allTaskLists = lists });
  }
  private assignTask() {
    this.task = this.data.task ? this.data.task : getDefaultBoardTask();
  }
  private initForm(): void {
    const firstListId = this.allTaskLists.length > 0 ? this.allTaskLists[0] : "";
    this.taskForm = this.formBuilder.group({
      name: [this.task?.name || '', Validators.required],
      listId: [this.task?.boardTaskListId || firstListId, Validators.required],
      dueDate: [this.task?.dueTime || new Date(), [Validators.required, this.dateValidator.dateMinimum(this.minDate)]],
      priority: [this.task?.priority || Priority.Low, [Validators.required]],
      description: [this.task?.description || ""]
    });
  }
  private getTaskFromForm() {
    const formValue = this.taskForm.value;
    const buffer = {
      ...this.task,
      name: formValue.name,
      boardTaskListId: formValue.listId,
      dueTime: formValue.dueDate,
      priority: formValue.priority,
      description: formValue.description,
    };
    return buffer;
  }
  private createNewTask(task: BoardTask) {
    this.taskService.createTask({ ...task, boardTaskListId: this.data.taskListId });
  }
  private updateTask(task: BoardTask) {
    this.taskListService.getTaskListById(this.task.boardTaskListId).subscribe(
      (list) => {
        if (list) {
          var currentTaskList = this.allTaskLists.find(x => x.id == task.boardTaskListId)!;
          var newIndex = currentTaskList.id == task.boardTaskListId ? list.boardTasks.findIndex((element) => element.id === this.task.id) : 0;
          this.taskService.updateTask(list, task, newIndex);
        }
      }
    );
  }
}

